mapper.delete:
delete from WAQ_ACTN_MSG
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT A.*
	  FROM WAQ_ACTN_MSG A
	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT ((SELECT NVL(MAX(RQST_SNO), 0) FROM WAQ_ACTN_MSG WHERE RQST_NO = #{reqmst.rqstNo}) + ROWNUM) AS RQST_SNO , 'I' AS IBS_STATUS , A.ACTN_MSG_ID, A.ACTN_MSG_LNM, A.ACTN_MSG_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS , A.ACTN_CD, A.ERR_TLG_LAN_DSCD, A.ACTN_MSG_CTNT, A.ACTN_USE_YN, A.ACTN_DEL_YN, A.ACTN_CD_APL_DT, A.CHNL_MSG_CD, A.CHNL_TYP_CD, A.MCI_LNK_YN, A.MCI_LNK_DT FROM WAM_ACTN_MSG A WHERE A.ACTN_MSG_ID IN
#{item.actnMsgId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.update:
 	UPDATE WAQ_ACTN_MSG SET ACTN_MSG_ID = #{actnMsgId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
