mapper.select:
select 

from WAA_DMNG_INFOTP_MAP
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
  and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
  AND EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD')

mapper.delete:
delete from WAA_DMNG_INFOTP_MAP
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
  and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}

mapper.update:
update WAA_DMNG_INFOTP_MAP
set EXP_DTM = #{expDtm,jdbcType=DATE},
  STR_DTM = #{strDtm,jdbcType=DATE},
  OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
  OBJ_VERS = #{objVers,jdbcType=DECIMAL},
  WRIT_DTM = #{writDtm,jdbcType=DATE},
  WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
  and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}

mapper.select:
SELECT A.DMNG_ID, A.INFOTP_ID, A.EXP_DTM, A.STR_DTM, A.OBJ_DESCN, A.OBJ_VERS, A.WRIT_DTM, A.WRIT_USER_ID, B.DMNG_LNM, B.DMNG_PNM, B.STND_ASRT, B.UPP_DMNG_ID, B.DMNG_LVL, C.INFOTP_LNM, C.DATA_TYPE, C.DATA_LEN, C.DATA_SCAL FROM WAA_DMNG_INFOTP_MAP A INNER JOIN WAA_DMNG B ON A.DMNG_ID = B.DMNG_ID AND B.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_INFO_TYPE C ON A.INFOTP_ID = C.INFOTP_ID AND C.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') WHERE A.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD')
AND A.DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND A.INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND B.DMNG_PNM = #{dmngPnm,jdbcType=DATE}
AND B.DMNG_LNM=#{dmngLnm,jdbcType=DATE}
AND UPPER(C.INFOTP_LNM) LIKE '%' || UPPER(#{infotpLnm,jdbcType=VARCHAR}) || '%'
#{dataType,jdbcType=DECIMAL}
#{dataLen,jdbcType=DATE}
#{dataScal,jdbcType=VARCHAR}
AND B.UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
#{dmngLvl,jdbcType=VARCHAR}
AND B.STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}
ORDER BY B.STND_ASRT,DMNG_LNM, INFOTP_LNM

mapper.update:
UPDATE WAA_DMNG_INFOTP_MAP SET
	EXP_DTM = NOW()
WHERE DMNG_ID = #{dmngId}
  AND INFOTP_ID = #{infotpId}
  AND EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
