mapper.select:
select

from WAA_GOV_STWD


		AND STWD_ID = #{stwdId,jdbcType=VARCHAR}


		AND ENACT_DGR = #{enactDgr,jdbcType=VARCHAR}


		AND STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}


		AND STWD_PNM = #{stwdPnm,jdbcType=DECIMAL}


		AND ENG_NM = #{engNm,jdbcType=VARCHAR}


		AND OBJ_DESCN = #{objDescn,jdbcType=VARCHAR}


		AND DMN_YN = #{dmnYn,jdbcType=VARCHAR}


		AND DMN_LNM = #{dmnLnm,jdbcType=VARCHAR}


		AND SYNONYMS = #{synonyms,jdbcType=VARCHAR}


		AND PROHIBITS = #{prohibits,jdbcType=VARCHAR}

mapper.delete:
delete from WAA_DMNG_INFOTP_MAP
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}

delete from WAA_GOV_STWD
where STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}
and STWD_PNM = #{stwdPnm,jdbcType=VARCHAR}

mapper.update:
update WAA_DMNG_INFOTP_MAP
set EXP_DTM = #{expDtm,jdbcType=DATE},
STR_DTM = #{strDtm,jdbcType=DATE},
OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
OBJ_VERS = #{objVers,jdbcType=DECIMAL},
WRIT_DTM = #{writDtm,jdbcType=DATE},
WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}

UPDATE WAA_GOV_STWD SET
APL_DTM = NOW()
WHERE STWD_LNM = #{stwdLnm}
AND STWD_PNM = #{stwdPnm}

mapper.delete:
delete from WAA_GOV_SDITM
where SDITM_LNM = #{sditmLnm,jdbcType=VARCHAR}
and SDITM_PNM = #{sditmPnm,jdbcType=VARCHAR}

mapper.update:
update WAA_GOV_SDITM


		AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}


		AND ENACT_DGR = #{enactDgr,jdbcType=VARCHAR}


		AND SDITM_LNM = #{sditmLnm,jdbcType=VARCHAR}


		AND SDITM_PNM = #{sditmPnm,jdbcType=DECIMAL}


		AND OBJ_DESCN = #{objDescn,jdbcType=VARCHAR}


		AND DMN_ID = #{dmnId,jdbcType=VARCHAR}


		AND DMN_LNM = #{dmnLnm,jdbcType=VARCHAR}


		AND SAVE_TYPE = #{saveType,jdbcType=VARCHAR}


		AND EXAM_DATA = #{examData,jdbcType=VARCHAR}


		AND CD_VAL_RULE = #{cdValRule,jdbcType=VARCHAR}


		AND GOV_STND_CD = #{govStndCd,jdbcType=VARCHAR}


		AND GOV_ORG_NM = #{govOrgNm,jdbcType=VARCHAR}


where SDITM_ID = #{sditmId,jdbcType=VARCHAR}

update WAA_GOV_SDITM
set EXP_DTM = #{expDtm,jdbcType=DATE},
STR_DTM = #{strDtm,jdbcType=DATE},
OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
OBJ_VERS = #{objVers,jdbcType=DECIMAL},
WRIT_DTM = #{writDtm,jdbcType=DATE},
WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
where DMNG_ID = #{dmngId,jdbcType=VARCHAR}
and INFOTP_ID = #{infotpId,jdbcType=VARCHAR}

UPDATE WAA_GOV_SDITM
SET APL_DTM = NOW()
WHERE SDITM_LNM = #{sditmLnm}
AND SDITM_PNM = #{sditmPnm}

UPDATE WAA_GOV_DMN 
SET APL_DTM = NOW()
WHERE DMN_LNM = #{dmnLnm}
AND DMN_PNM = #{dmnPnm}

mapper.delete:
DELETE FROM WAA_GOV_DMN
WHERE DMN_LNM = #{dmnLnm,jdbcType=VARCHAR}
AND DMNG_LNM = #{dmngLnm,jdbcType=VARCHAR}

DELETE FROM WAA_GOV_CD_VAL
WHERE CD_VAL = #{cdVal,jdbcType=VARCHAR}
AND CD_VAL_NM = #{cdValNm,jdbcType=VARCHAR}
