mapper.update:
UPDATE WAA_USER
SET REG_TYP_CD = #{regTypCd}
where USER_ID = #{userId,jdbcType=VARCHAR}

mapper.select:
SELECT
A.USER_ID, A.EXP_DTM, A.STR_DTM, A.LOGIN_AC_ID, A.LOGIN_AC_PWD, A.LOGIN_AC_PWD_ENC, A.USER_NM, A.USERG_ID, A.DEPT_ID, A.JGD_NM, A.USER_TELNO, A.USER_HTELNO, A.EMAIL_ADDR, A.EXCL_DWLD_AUTH_YN, A.ID_USE_EXP_DT, A.PWD_EXP_DT, A.OBJ_DESCN, A.OBJ_VERS, A.REG_TYP_CD, A.RQST_DTM, A.RQST_USER_ID, A.APRV_DCD, A.APRV_DTM, A.APRV_USER_ID, C.DEPT_NM, C.DEPT_PATH , CASE WHEN C.DEPT_ID = (SELECT A.DEPT_ID FROM WAA_USER A WHERE A.USER_ID = #{userId} AND A.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U')) THEN 0 ELSE NULL END AS DEPT_SEQ FROM WAA_USER A
LEFT OUTER JOIN ( SELECT A.DEPT_ID , B.DEPT_ID AS UPP_DEPT_ID , A.DEPT_NM , A.DEPT_LVL , A.ALL_DEPT_NM AS DEPT_PATH FROM (
) A LEFT OUTER JOIN WAA_DEPT B ON A.UPP_DEPT_ID = B.DEPT_ID AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND B.REG_TYP_CD IN ('C','U')
AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
) C ON A.DEPT_ID = C.DEPT_ID where 1=1 AND A.EXP_DTM = TO_DATE ('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U')
AND A.USER_NM LIKE '%' || #{userNm,jdbcType=VARCHAR} || '%'
AND A.USERG_ID = #{usergId,jdbcType=VARCHAR}
AND C.DEPT_NM LIKE '%' || #{deptNm,jdbcType=VARCHAR} || '%'
AND A.DEPT_ID = #{deptId,jdbcType=VARCHAR}
AND A.USER_TELNO = #{userTelno,jdbcType=VARCHAR}
AND A.USER_HTELNO = #{userHtelno,jdbcType=VARCHAR}
AND A.USER_ID IN (select Y.USER_ID from WAA_APRG X INNER JOIN WAA_APRG_USER Y ON X.APRG_ID =Y.APRG_ID WHERE X.APRG_ID = 'OBJ_00000007348' AND X.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND X.REG_TYP_CD IN ('C', 'U') AND Y.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND Y.REG_TYP_CD IN ('C', 'U'))
ORDER BY DEPT_SEQ, C.DEPT_PATH, A.USER_NM

SELECT A.USER_ID, A.EXP_DTM, A.STR_DTM, A.LOGIN_AC_ID, A.USER_NM, A.USERG_ID, A.DEPT_ID, A.JGD_NM, A.USER_TELNO, A.USER_HTELNO, A.EMAIL_ADDR, A.EXCL_DWLD_AUTH_YN, A.ID_USE_EXP_DT, A.PWD_EXP_DT, A.OBJ_DESCN, A.OBJ_VERS, A.REG_TYP_CD, A.RQST_DTM, A.RQST_USER_ID, A.APRV_DCD, A.APRV_DTM, A.APRV_USER_ID, B.USERG_LNM, C.DEPT_NM, A.SALT_KEY, A.IS_LOCK FROM WAA_USER A INNER JOIN WAA_USERG B --LEFT OUTER JOIN WAA_DEPT C ON A.USERG_ID = B.USERG_ID AND B.REG_TYP_CD IN ('C', 'U') AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_DEPT C ON A.DEPT_ID = C.DEPT_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND C.REG_TYP_CD IN ('C', 'U')
A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U')
AND A.USER_ID = #{userId,jdbcType=VARCHAR}
AND UPPER(A.USER_NM) LIKE '%' || UPPER(#{userNm,jdbcType=VARCHAR}) || '%'
AND A.USERG_ID = #{usergId,jdbcType=VARCHAR}
AND C.DEPT_NM LIKE '%' || #{deptNm,jdbcType=VARCHAR} || '%'
AND A.DEPT_ID = #{deptId,jdbcType=VARCHAR}
AND A.USER_TELNO = #{userTelno,jdbcType=VARCHAR}
AND A.USER_HTELNO = #{userHtelno,jdbcType=VARCHAR}
ORDER BY USER_NM

SELECT COUNT(*) FROM WAA_USER A
AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.LOGIN_AC_ID = #{loginAcId,jdbcType=VARCHAR}
AND USER_ID != #{userId,jdbcType=VARCHAR}
AND COALESCE(A.IS_LOCK, 'N') = 'N'

SELECT COUNT(*)
  FROM WAA_USER
 WHERE 1=1
   AND REG_TYP_CD IN ('C', 'U')
   AND EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
   AND USER_ID = #{userId,jdbcType=VARCHAR}
   AND LOGIN_AC_PWD = #{loginAcPwd,jdbcType=VARCHAR}

   AND LOGIN_AC_PWD_ENC = #{loginAcPwdEnc,jdbcType=VARCHAR}

   AND IS_LOCK = 'N'

SELECT COUNT(*) FROM WAA_USER
WHERE USER_ID= #{userId}
  AND EXP_DTM = TO_DATE('99991231','YYYYMMDD')
