mapper.select:
select 

from WAM_PRF_RESULT
where PRF_ID = #{prfId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm,jdbcType=TIMESTAMP}

mapper.delete:
delete from WAM_PRF_RESULT
where PRF_ID = #{prfId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm,jdbcType=TIMESTAMP}

mapper.update:
update WAM_PRF_RESULT
set ANA_END_DTM = #{anaEndDtm,jdbcType=TIMESTAMP},
  ANA_DGR = #{anaDgr,jdbcType=DECIMAL},
  ANA_CNT = #{anaCnt,jdbcType=DECIMAL},
  ESN_ER_CNT = #{esnErCnt,jdbcType=DECIMAL},
  ANA_LOG_ID = #{anaLogId,jdbcType=VARCHAR},
  ANA_USER_ID = #{anaUserId,jdbcType=VARCHAR}
where PRF_ID = #{prfId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm,jdbcType=TIMESTAMP}

mapper.select:
SELECT M.DB_CONN_TRG_ID ,M.DB_SCH_ID ,M.PRF_ID ,M.PRF_KND_CD AS PRF_KND_CD_NM ,M.PRF_KND_CD ,M.PRF_NM ,DQI.FULL_PATH AS DQI_LNM ,M.DQI_ID ,M.DBC_TBL_NM ,M.OBJ_NM ,R.ANA_STR_DTM ,R.ANA_END_DTM ,R.ANA_DGR ,R.ANA_CNT ,R.ESN_ER_CNT ,R.ANA_LOG_ID ,GET_USER_NM(R.ANA_USER_ID) AS ANA_USER_NM ,(CASE WHEN R.ANA_CNT IS NULL THEN NULL ELSE (CASE WHEN R.ANA_CNT = '0' THEN '0' ELSE ROUND(R.ESN_ER_CNT*100/R.ANA_CNT,2)::VARCHAR END ) END ) AS ESN_ER_RATE ,(CASE WHEN R.ESN_ER_CNT IS NULL THEN NULL ELSE (CASE WHEN R.ESN_ER_CNT = '0' THEN NULL ELSE '#FF0000' END ) END ) AS FontColor FROM WAA_DB_CONN_TRG DB INNER JOIN WAA_DB_SCH S ON DB.DB_CONN_TRG_ID = S.DB_CONN_TRG_ID AND S.EXP_DTM = TO_DATE('99991231','YYYYMMDD') AND S.REG_TYP_CD IN ('C','U') INNER JOIN WAM_PRF_MSTR M ON DB.DB_CONN_TRG_ID = M.DB_CONN_TRG_ID AND S.DB_SCH_ID = M.DB_SCH_ID AND M.REG_TYP_CD IN ('C','U')
LEFT OUTER JOIN ( ) R ON M.PRF_ID = R.PRF_ID LEFT OUTER JOIN WAM_DQI DQI ON DQI.DQI_ID = M.DQI_ID AND DQI.REG_TYP_CD IN ('C', 'U') WHERE DB.EXP_DTM = TO_DATE('99991231','YYYYMMDD') AND DB.REG_TYP_CD IN ('C','U')
AND M.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND M.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND M.DBC_TBL_NM = #{dbcTblNm,jdbcType=VARCHAR}
AND M.OBJ_NM = #{objNm,jdbcType=VARCHAR}
AND M.OBJ_NM = #{dbcColNm,jdbcType=VARCHAR}
AND M.PRF_KND_CD LIKE #{tblColGb}||'%'
AND M.PRF_ID = #{prfId,jdbcType=VARCHAR}
AND R.ANA_STR_DTM = TO_DATE(#{schAnaStrDtm,jdbcType=VARCHAR},'YYYYMMDD HH24:MI:SS')
AND R.ANA_DGR = #{anaDgr,jdbcType=DECIMAL}
ORDER BY M.PRF_KND_CD

SELECT A.*
,ROUND(WDQ_FC_SIGMA(WDQ_FC_DPMO(A.ESN_ER_CNT, A.ANA_CNT ))::NUMERIC,2)::VARCHAR AS SIGMA ,CASE WHEN A.ANA_CNT=0 THEN 0 ELSE CASE WHEN A.ESN_ER_CNT=0 THEN 100 ELSE ROUND(((A.ANA_CNT-A.ESN_ER_CNT)/A.ANA_CNT*100),2) END END AS ER_RATE FROM ( SELECT PM.DB_CONN_TRG_ID ,DB.DB_CONN_TRG_LNM ,PR.ANA_DGR ,CONCAT(PR.ANA_DGR, ' 차') AS ANA_DGR_DISP ,PM.PRF_KND_CD ,MAX(PR.ANA_STR_DTM) AS ANA_STR_DTM ,SUM(COALESCE(PR.ANA_CNT,0)) AS ANA_CNT ,SUM(COALESCE(PR.ESN_ER_CNT,0)) AS ESN_ER_CNT FROM WAM_PRF_RESULT PR INNER JOIN WAM_PRF_MSTR PM ON PR.PRF_ID = PM.PRF_ID AND PM.REG_TYP_CD IN ('C', 'U') INNER JOIN WAT_DBC_TBL TDB ON PM.DB_CONN_TRG_ID = TDB.DB_CONN_TRG_ID AND PM.DB_SCH_ID = TDB.DB_SCH_ID AND PM.DBC_TBL_NM = TDB.DBC_TBL_NM INNER JOIN WAA_DB_CONN_TRG DB ON PM.DB_CONN_TRG_ID = DB.DB_CONN_TRG_ID AND DB.REG_TYP_CD IN ('C', 'U') AND DB.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_DB_SCH SCH ON DB.DB_CONN_TRG_ID = SCH.DB_CONN_TRG_ID AND SCH.REG_TYP_CD IN ('C', 'U') AND SCH.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_EXP_TBL EX ON EX.DB_SCH_ID = PM.DB_SCH_ID AND EX.DBC_TBL_NM =PM.DBC_TBL_NM WHERE PR.ANA_DGR IS NOT NULL AND COALESCE(EX.EXP_YN,'N')='N' AND PM.PRF_KND_CD != 'PC01'
AND PM.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND PM.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND PM.PRF_KND_CD = #{prfKndCd,jdbcType=VARCHAR}
AND (PM.DBC_TBL_NM LIKE '%' || #{dbcTblNm,jdbcType=VARCHAR} || '%' OR PM.DBC_TBL_NM LIKE '%' || UPPER(#{dbcTblNm,jdbcType=VARCHAR}) || '%')
AND (PM.OBJ_NM LIKE '%' || #{dbcColNm,jdbcType=VARCHAR} || '%' OR PM.OBJ_NM LIKE '%' || UPPER(#{dbcColNm,jdbcType=VARCHAR}) || '%')
AND PR.ANA_DGR = #{anaDgr,jdbcType=VARCHAR}
GROUP BY PR.ANA_DGR, PM.PRF_KND_CD, PM.DB_CONN_TRG_ID, DB.DB_CONN_TRG_LNM ) A
ORDER BY DB_CONN_TRG_LNM, PRF_KND_CD, ANA_DGR
