mapper.select:
SELECT A.${objIdCol} , A.${erDataSnoCol} AS ER_DATA_SNO , A.COL_CNT ${colNm}
,CASE WHEN P.PRF_ID IS NULL THEN NULL ELSE '#FF0000' END AS FontColor ,CASE WHEN P.PRF_ID IS NULL THEN NULL ELSE '1' END AS PK_DATA_YN
FROM ${objErrTbl} A
LEFT OUTER JOIN (SELECT PRF_ID, ANA_STR_DTM, ESN_ER_DATA_SNO FROM WAM_PRF_ERR_DATA_PKDATA GROUP BY PRF_ID, ANA_STR_DTM, ESN_ER_DATA_SNO ) P ON A.PRF_ID = P.PRF_ID AND A.ANA_STR_DTM = P.ANA_STR_DTM AND A.ESN_ER_DATA_SNO = P.ESN_ER_DATA_SNO
WHERE A.${erDataSnoCol} <> 0 AND A.${objIdCol} = #{objId, jdbcType=VARCHAR} AND A.ANA_STR_DTM = TO_TIMESTAMP('${objDate}','YYYY-MM-DD HH24:MI:SS') ORDER BY A.${erDataSnoCol}

SELECT A.${objIdCol} , A.${erDataSnoCol} AS ER_DATA_SNO , A.COL_CNT ${colNm}
,CASE WHEN P.PRF_ID IS NULL THEN NULL ELSE '#FF0000' END AS FontColor ,CASE WHEN P.PRF_ID IS NULL THEN NULL ELSE '1' END AS PK_DATA_YN
FROM ${objErrTbl} A
LEFT OUTER JOIN (SELECT PRF_ID, ANA_STR_DTM, ESN_ER_DATA_SNO FROM WAM_PRF_ERR_DATA_PKDATA GROUP BY PRF_ID, ANA_STR_DTM, ESN_ER_DATA_SNO ) P ON A.PRF_ID = P.PRF_ID AND A.ANA_STR_DTM = P.ANA_STR_DTM AND A.ESN_ER_DATA_SNO = P.ESN_ER_DATA_SNO
WHERE A.${erDataSnoCol} <> 0 AND A.${objIdCol} = #{objId, jdbcType=VARCHAR} AND A.ANA_STR_DTM = (SELECT MAX(ANA_STR_DTM) FROM WAM_PRF_ERR_DATA WHERE PRF_ID = #{objId, jdbcType=VARCHAR}) AND A.${erDataSnoCol} <= 100 ORDER BY A.${erDataSnoCol}

mapper.update:
UPDATE WAM_PRF_MSTR
   SET PRFC_SCRIPT = #{prfcScript, jdbcType=VARCHAR}
 WHERE PRF_ID = #{objId, jdbcType=VARCHAR}
   AND REG_TYP_CD IN ('C', 'U')
