mapper.select:
SELECT A.* ,GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM FROM WAH_DDL_COL A INNER JOIN WAQ_DDL_COL B ON B.DDL_COL_ID = A.DDL_COL_ID WHERE A.DDL_COL_ID = #{ddlColId,jdbcType=VARCHAR}
AND A.REG_TYP_CD = #{regTypCd, jdbcType=VARCHAR}
AND B.RQST_NO = #{rqstNo, jdbcType=VARCHAR} -- AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')

SELECT A.DDL_COL_ID ,A.DDL_COL_PNM ,A.DDL_COL_LNM ,A.DDL_TBL_ID ,A.COL_ORD
, CASE WHEN A.DATA_LEN IS NULL THEN A.DATA_TYPE ELSE (CASE WHEN A.DATA_SCAL IS NOT NULL THEN CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ', ', A.DATA_SCAL, ')' ) ELSE CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ')') END ) END AS DATA_TYPE ,A.DATA_LEN ,A.DATA_SCAL ,COALESCE(A.PK_YN, 'N') AS PK_YN ,A.PK_ORD ,COALESCE(A.NONUL_YN, 'N') AS NONUL_YN ,A.DEFLT_VAL ,A.RQST_NO ,A.RQST_SNO ,A.RQST_DTL_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM ,B.DDL_TBL_PNM ,B.DDL_TBL_LNM ,B.SCRT_INFO ,COALESCE(NULLIF(A.ENC_YN, ''), 'N') AS ENC_YN FROM WAH_DDL_COL A INNER JOIN WAH_DDL_TBL B ON A.DDL_TBL_ID = B.DDL_TBL_ID AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_DB_SCH C ON C.DB_SCH_ID = B.DB_SCH_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND C.REG_TYP_CD IN ('C', 'U')
AND A.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
AND B.DDL_TRG_DCD = #{ddlTrgDcd,jdbcType=VARCHAR}
AND (B.DDL_TBL_PNM LIKE '%' || #{ddlTblPnm,jdbcType=VARCHAR} || '%' OR B.DDL_TBL_LNM LIKE '%' || #{ddlTblPnm,jdbcType=VARCHAR} || '%' OR B.DDL_TBL_PNM LIKE '%' || UPPER(#{ddlTblPnm,jdbcType=VARCHAR}) || '%' OR B.DDL_TBL_LNM LIKE '%' || UPPER(#{ddlTblPnm,jdbcType=VARCHAR}) || '%')
AND (A.DDL_COL_PNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_LNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_PNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%' OR A.DDL_COL_LNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%')
AND C.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND A.REG_TYP_CD IN ('C', 'U')
ORDER BY A.DDL_TBL_ID, COL_ORD

SELECT A.* , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM , CASE WHEN A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U') THEN '#0000FF' ELSE NULL END AS FontColor FROM WAH_DDL_COL A
AND A.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
ORDER BY A.DDL_COL_PNM, A.OBJ_VERS DESC, A.EXP_DTM DESC, A.STR_DTM DESC

SELECT A.DDL_COL_ID , A.DDL_COL_PNM , A.DDL_COL_LNM , A.DDL_TBL_ID , A.COL_ORD , CASE WHEN A.DATA_LEN IS NULL THEN A.DATA_TYPE ELSE (CASE WHEN A.DATA_SCAL IS NOT NULL THEN CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ', ', A.DATA_SCAL, ')' ) ELSE CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ')' ) END ) END AS DATA_TYPE , A.DATA_LEN , A.DATA_SCAL , COALESCE(A.PK_YN, 'N') AS PK_YN , A.PK_ORD , COALESCE(A.NONUL_YN, 'N') AS NONUL_YN , A.DEFLT_VAL , A.RQST_NO , A.RQST_SNO , A.RQST_DTL_SNO , A.OBJ_DESCN , A.OBJ_VERS , A.REG_TYP_CD , A.FRS_RQST_DTM , A.FRS_RQST_USER_ID , A.RQST_DTM , A.RQST_USER_ID , A.APRV_DTM , A.APRV_USER_ID , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM , B.DDL_TBL_PNM , B.DDL_TBL_LNM FROM WAM_DDL_COL A INNER JOIN WAM_DDL_TBL B ON A.DDL_TBL_ID = B.DDL_TBL_ID WHERE 1 = 1
AND A.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
AND B.DDL_TRG_DCD = #{ddlTrgDcd,jdbcType=VARCHAR}
AND B.DDL_TBL_PNM = #{ddlTblPnm,jdbcType=VARCHAR}
AND (A.DDL_COL_PNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_LNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_PNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%' OR A.DDL_COL_LNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%')
ORDER BY A.DDL_TBL_ID, COL_ORD

SELECT A.DDL_COL_ID ,A.DDL_COL_PNM ,A.DDL_COL_LNM ,A.DDL_TBL_ID ,A.COL_ORD
, CASE WHEN A.DATA_LEN IS NULL THEN A.DATA_TYPE ELSE (CASE WHEN A.DATA_SCAL IS NOT NULL THEN CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ', ', A.DATA_SCAL, ')' ) ELSE CONCAT(A.DATA_TYPE, '(', A.DATA_LEN, ')') END ) END AS DATA_TYPE ,A.DATA_LEN ,A.DATA_SCAL ,COALESCE(A.PK_YN, 'N') AS PK_YN ,A.PK_ORD ,COALESCE(A.NONUL_YN, 'N') AS NONUL_YN ,A.DEFLT_VAL ,A.RQST_NO ,A.RQST_SNO ,A.RQST_DTL_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,COALESCE(NULLIF(A.ENC_YN, ''), 'N') AS ENC_YN ,GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM ,GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM ,GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM ,B.DDL_TBL_PNM ,B.DDL_TBL_LNM ,B.SCRT_INFO FROM WAH_DDL_TBL B INNER JOIN ( SELECT Y.* FROM WAQ_DDL_TBL X INNER JOIN WAQ_DDL_COL Y ON X.RQST_NO = Y.RQST_NO AND X.DDL_TBL_ID = Y.DDL_TBL_ID ORDER BY X.RQST_NO, Y.COL_ORD
) A ON A.RQST_NO = B.RQST_NO AND A.DDL_TBL_ID = B.DDL_TBL_ID INNER JOIN WAA_DB_SCH C ON C.DB_SCH_ID = B.DB_SCH_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND C.REG_TYP_CD IN ('C', 'U')
AND A.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
AND B.DDL_TRG_DCD = #{ddlTrgDcd,jdbcType=VARCHAR}
AND (B.DDL_TBL_PNM LIKE '%' || #{ddlTblPnm,jdbcType=VARCHAR} || '%' OR B.DDL_TBL_LNM LIKE '%' || #{ddlTblPnm,jdbcType=VARCHAR} || '%' OR B.DDL_TBL_PNM LIKE '%' || UPPER(#{ddlTblPnm,jdbcType=VARCHAR}) || '%' OR B.DDL_TBL_LNM LIKE '%' || UPPER(#{ddlTblPnm,jdbcType=VARCHAR}) || '%')
AND (A.DDL_COL_PNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_LNM LIKE '%' || #{ddlColLnm,jdbcType=VARCHAR} || '%' OR A.DDL_COL_PNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%' OR A.DDL_COL_LNM LIKE '%' || UPPER(#{ddlColLnm,jdbcType=VARCHAR}) || '%')
AND C.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND B.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND B.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND (NULLIF(A.REG_TYP_CD, '') IS NULL OR A.REG_TYP_CD != 'D') ORDER BY A.DDL_TBL_ID, COL_ORD
