mapper.select:
SELECT A.DDL_GRT_ID , A.GRTOR_DB_ID , A.GRTOR_DB_PNM , A.GRTOR_SCH_ID , A.GRTOR_SCH_PNM , A.DDL_OBJ_ID , A.DDL_OBJ_PNM , A.DDL_OBJ_TYP_CD , A.GRTED_DB_ID , A.GRTED_DB_PNM , A.GRTED_SCH_ID , A.GRTED_SCH_PNM , A.SELECT_YN , A.INSERT_YN , A.UPDATE_YN , A.DELETE_YN , A.EXECUTE_YN , A.SYNONYM_YN , A.SCRT_INFO , COALESCE(H.PRC_TYP_CD,'NPC') AS PRC_TYP_CD , H.PRC_DT , H.PRC_DBA_ID , A.RQST_NO , A.RQST_SNO , A.OBJ_DESCN , A.OBJ_VERS , A.REG_TYP_CD , A.FRS_RQST_DTM , A.FRS_RQST_USER_ID , A.RQST_DTM , A.RQST_USER_ID , A.APRV_DTM , A.APRV_USER_ID , A.RQST_RESN , A.DDL_OBJ_LNM , DB.DB_CONN_TRG_PNM AS GRTOR_DB_PNM , S.DB_SCH_PNM AS GRTOR_SCH_PNM , DB2.DB_CONN_TRG_PNM AS GRTED_DB_PNM , S2.ROLE_PNM AS GRTED_SCH_PNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM FROM WAM_DDL_GRT A INNER JOIN WAA_DB_SCH S ON A.GRTOR_SCH_ID = S.DB_SCH_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_DB_CONN_TRG DB ON S.DB_CONN_TRG_ID = DB.DB_CONN_TRG_ID AND DB.REG_TYP_CD IN ('C', 'U') AND DB.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
INNER JOIN WAA_DB_ROLE S2 ON A.GRTED_SCH_ID = S2.ROLE_ID AND S2.REG_TYP_CD IN ('C', 'U') AND S2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_DB_CONN_TRG DB2 ON S2.DB_CONN_TRG_ID = DB2.DB_CONN_TRG_ID AND DB2.REG_TYP_CD IN ('C', 'U') AND DB2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAH_DDL_GRT H ON A.DDL_GRT_ID = H.DDL_GRT_ID AND H.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U1.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U2.REG_TYP_CD IN ('C', 'U') WHERE 1 = 1 AND A.REG_TYP_CD IN ('C', 'U')
AND (A.DDL_OBJ_PNM LIKE '%' || #{ddlObjPnm,jdbcType=VARCHAR} || '%' OR A.DDL_OBJ_PNM LIKE '%' || UPPER(#{ddlObjPnm,jdbcType=VARCHAR}) || '%')
AND S.DB_SCH_ID = #{grtorSchId,jdbcType=VARCHAR}
AND DB.DB_CONN_TRG_ID = #{grtorDbId,jdbcType=VARCHAR}
AND S2.ROLE_ID = #{grtedSchId,jdbcType=VARCHAR}
AND DB2.DB_CONN_TRG_ID = #{grtedDbId,jdbcType=VARCHAR}
AND A.DDL_GRT_ID = #{ddlGrtId,jdbcType=VARCHAR}
AND A.OBJ_DESCN LIKE '%' || #{objDescn,jdbcType=VARCHAR} || '%'
AND A.DDL_OBJ_TYP_CD = #{ddlObjTypCd,jdbcType=VARCHAR}
ORDER BY S.DB_SCH_PNM , A.DDL_OBJ_PNM

SELECT A.* , DB.DB_CONN_TRG_PNM AS GRTOR_DB_PNM , S.DB_SCH_PNM AS GRTOR_SCH_PNM , DB2.DB_CONN_TRG_PNM AS GRTED_DB_PNM , S2.ROLE_PNM AS GRTED_SCH_PNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM FROM WAH_DDL_GRT A LEFT JOIN WAA_DB_SCH S ON A.GRTOR_SCH_ID = S.DB_SCH_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_DB_CONN_TRG DB ON S.DB_CONN_TRG_ID = DB.DB_CONN_TRG_ID AND DB.REG_TYP_CD IN ('C', 'U') AND DB.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
LEFT JOIN WAA_DB_ROLE S2 ON A.GRTED_SCH_ID = S2.ROLE_ID AND S2.REG_TYP_CD IN ('C', 'U') AND S2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAA_DB_CONN_TRG DB2 ON S2.DB_CONN_TRG_ID = DB2.DB_CONN_TRG_ID AND DB2.REG_TYP_CD IN ('C', 'U') AND DB2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U1.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U2.REG_TYP_CD IN ('C', 'U')
AND (A.DDL_OBJ_PNM LIKE '%' || #{ddlObjPnm,jdbcType=VARCHAR} || '%' OR A.DDL_OBJ_PNM LIKE '%' || UPPER(#{ddlObjPnm,jdbcType=VARCHAR}) || '%')
AND S.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND DB.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND TO_CHAR(A.APRV_DTM, 'YYYY-MM-DD') >= #{searchBgnDe}
AND TO_CHAR(A.APRV_DTM, 'YYYY-MM-DD') <= #{searchEndDe}
ORDER BY A.DDL_OBJ_PNM, A.OBJ_VERS
