mapper.select:
SELECT A.* , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM , D.DATA_TYPE , D.DATA_LEN , D.DATA_SCAL , D.PK_YN , D.PK_ORD , D.NONUL_YN , D.DEFLT_VAL , B.DDL_IDX_LNM , C.DDL_TBL_PNM , C.DDL_TBL_LNM , S.FULL_PATH , B.DDL_IDX_PNM FROM WAH_DDL_IDX_COL A INNER JOIN WAH_DDL_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAH_DDL_TBL C ON B.DDL_TBL_ID = C.DDL_TBL_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAM_PDM_TBL P ON C.PDM_TBL_ID = P.PDM_TBL_ID AND P.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_SUBJ S ON P.SUBJ_ID = S.SUBJ_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAH_DDL_COL D ON A.DDL_COL_ID = D.DDL_COL_ID AND D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND C.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
AND C.DDL_TBL_PNM = #{ddlTblPnm,jdbcType=VARCHAR}
AND S.FULL_PATH = #{fullPath,jdbcType=VARCHAR}
ORDER BY B.DDL_IDX_PNM , A.DDL_IDX_COL_ORD , A.DDL_IDX_COL_PNM

SELECT A.* , B.DDL_IDX_PNM , B.DDL_IDX_LNM , C.DDL_TBL_PNM , C.DDL_TBL_LNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM FROM WAQ_DDL_IDX_COL A LEFT JOIN WAH_DDL_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO AND A.RQST_DTL_SNO = B.RQST_DTL_SNO AND A.RQST_DCD = 'CU' INNER JOIN WAM_DDL_TBL C ON B.DDL_TBL_ID = C.DDL_TBL_ID LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.REG_TYP_CD IN ('C', 'U') AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND B.DDL_IDX_ID = #{ddlIdxId, jdbcType=VARCHAR} AND B.RQST_NO = #{rqstNo, jdbcType=VARCHAR}
ORDER BY A.DDL_IDX_COL_ORD, A.DDL_IDX_COL_PNM

SELECT A.* , B.DDL_IDX_PNM , B.DDL_IDX_LNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM , CASE WHEN A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U') THEN '#0000FF' ELSE NULL END AS FontColor FROM WAH_DDL_IDX_COL A LEFT JOIN WAH_DDL_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND A.RQST_NO = B.RQST_NO LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.REG_TYP_CD IN ('C', 'U') AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.REG_TYP_CD IN ('C', 'U') AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND A.DDL_IDX_ID = #{ddlIdxId}
AND B.DDL_TBL_ID = #{ddlTblId}
ORDER BY A.DDL_IDX_COL_ORD, A.EXP_DTM DESC, A.STR_DTM DESC

SELECT A.* , B.DDL_IDX_PNM , B.DDL_IDX_LNM , C.DDL_TBL_PNM , C.DDL_TBL_LNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM , CASE WHEN A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U') THEN '#0000FF' ELSE NULL END AS FontColor FROM WAH_DDL_IDX_COL A LEFT JOIN WAH_DDL_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAH_DDL_TBL C ON B.DDL_TBL_ID = C.DDL_TBL_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.REG_TYP_CD IN ('C', 'U') AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.REG_TYP_CD IN ('C', 'U') AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND B.DDL_IDX_ID = #{ddlIdxId}
AND C.DDL_TBL_ID = #{ddlTblId}
AND A.OBJ_VERS = #{objVers}
ORDER BY A.EXP_DTM DESC, A.STR_DTM DESC

SELECT A.* , GET_USER_NM(A.FRS_RQST_USER_ID) AS FRS_RQST_USER_NM , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM , GET_USER_NM(A.APRV_USER_ID) AS APRV_USER_NM , D.DATA_TYPE , D.DATA_LEN , D.DATA_SCAL , D.PK_YN , D.PK_ORD , D.NONUL_YN , D.DEFLT_VAL , B.DDL_IDX_LNM , C.DDL_TBL_PNM , C.DDL_TBL_LNM , S.FULL_PATH FROM WAH_DDL_IDX_COL A INNER JOIN WAH_DDL_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAH_DDL_TBL C ON B.DDL_TBL_ID = C.DDL_TBL_ID AND C.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAM_PDM_TBL P ON C.PDM_TBL_ID = P.PDM_TBL_ID AND P.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_SUBJ S ON P.SUBJ_ID = S.SUBJ_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAH_DDL_COL D ON A.DDL_COL_ID = D.DDL_COL_ID AND D.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND C.DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
AND C.DDL_TBL_PNM = #{ddlTblPnm,jdbcType=VARCHAR}
AND S.FULL_PATH = #{fullPath,jdbcType=VARCHAR}
ORDER BY A.DDL_IDX_ID, DDL_IDX_COL_ORD, DDL_IDX_COL_PNM
