mapper.select:
select 

from WAM_DDL_REL
where DDL_REL_ID = #{ddlRelId,jdbcType=VARCHAR}
  and PA_ENTY_ID = #{paEntyId,jdbcType=VARCHAR}
  and PA_ATTR_ID = #{paAttrId,jdbcType=VARCHAR}
  and CH_ENTY_ID = #{chEntyId,jdbcType=VARCHAR}
  and CH_ATTR_ID = #{chAttrId,jdbcType=VARCHAR}

mapper.delete:
delete from WAM_DDL_REL
where DDL_REL_ID = #{ddlRelId,jdbcType=VARCHAR}
  and PA_ENTY_ID = #{paEntyId,jdbcType=VARCHAR}
  and PA_ATTR_ID = #{paAttrId,jdbcType=VARCHAR}
  and CH_ENTY_ID = #{chEntyId,jdbcType=VARCHAR}
  and CH_ATTR_ID = #{chAttrId,jdbcType=VARCHAR}
