mapper.select:
SELECT , DB.DB_CONN_TRG_PNM , S.DB_SCH_PNM , U.USER_NM AS FRS_RQST_USER_NM , D.DEPT_NM AS RQST_USER_DEPT , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM , 'SEQ' AS OBJ_DCD , A.DDL_SEQ_ID AS OBJ_ID , A.RQST_RESN , A.APL_REQ_DT , A.APL_REQ_TYP_CD , A.PCKG_NM , A.SEQ_CLAS_CD , A.SEQ_INIT_CD , A.BIZ_IFNC , A.SCRT_INFO FROM WAM_DDL_SEQ A INNER JOIN WAA_DB_SCH S ON A.DB_SCH_ID = S.DB_SCH_ID AND S.REG_TYP_CD IN ('C', 'U') AND S.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') INNER JOIN WAA_DB_CONN_TRG DB ON S.DB_CONN_TRG_ID = DB.DB_CONN_TRG_ID AND DB.REG_TYP_CD IN ('C', 'U') AND DB.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U1.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_DEPT D ON U1.DEPT_ID = D.DEPT_ID AND D.EXP_DTM = TO_DATE('99991231','YYYYMMDD') AND D.REG_TYP_CD IN ('C','U') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND U2.REG_TYP_CD IN ('C', 'U')
AND UPPER(A.DDL_SEQ_PNM) LIKE '%' || UPPER(#{ddlSeqPnm,jdbcType=VARCHAR}) || '%'
AND S.DB_SCH_PNM LIKE '%'||#{dbSchPnm,jdbcType=VARCHAR}||'%'
AND S.DB_SCH_ID = #{dbSchId,jdbcType=VARCHAR}
AND DB.DB_CONN_TRG_PNM LIKE '%'||#{dbConnTrgPnm,jdbcType=VARCHAR}||'%'
AND DB.DB_CONN_TRG_ID = #{dbConnTrgId,jdbcType=VARCHAR}
AND UPPER(A.PDM_TBL_PNM) LIKE '%'||UPPER(#{pdmTblPnm,jdbcType=VARCHAR})||'%'
AND UPPER(A.PDM_COL_PNM) LIKE '%' || UPPER(#{pdmColPnm,jdbcType=VARCHAR}) || '%'
AND A.NM_RL_TYP_CD = #{nmRlTypCd,jdbcType=VARCHAR}
AND A.US_TYP_CD = #{usTypCd,jdbcType=VARCHAR}
AND A.DDL_TRG_DCD = #{ddlTrgDcd,jdbcType=VARCHAR}
AND A.SR_MNG_NO LIKE '%'||#{srMngNo,jdbcType=VARCHAR}||'%'
AND A.PRJ_MNG_NO LIKE '%'||#{prjMngNo,jdbcType=VARCHAR}||'%'
