mapper.select:
select 

from WAQ_DDL_IDX_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_IDX_COL
where RQST_NO          = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO         = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO     = #{rqstDtlSno,jdbcType=DECIMAL}
  and RQST_DTL_DTL_SNO = #{rqstDtlDtlSno,jdbcType=DECIMAL}

mapper.select:
SELECT
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' ELSE CASE WHEN A.VRF_CD = '5' THEN '#0000FF' END END AS FontColor , GET_USER_NM(A.RQST_USER_ID) AS RQST_USER_NM FROM WAQ_DDL_IDX_COL A INNER JOIN WAQ_DDL_IDX B ON B.RQST_NO = A.RQST_NO AND B.RQST_SNO = A.RQST_SNO AND B.RQST_DTL_SNO = A.RQST_DTL_SNO WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
AND A.RQST_DTL_SNO = #{rqstDtlSno}
ORDER BY B.DDL_IDX_PNM , A.DDL_IDX_COL_ORD

select 

from WAQ_DDL_IDX_COL
where RQST_NO          = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO         = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO     = #{rqstDtlSno,jdbcType=DECIMAL}
  and RQST_DTL_DTL_SNO = #{rqstDtlDtlSno,jdbcType=DECIMAL}

mapper.delete:
DELETE FROM WAQ_DDL_IDX_COL
 WHERE RQST_NO      = #{rqstNo}
   AND RQST_SNO     = #{rqstSno}
   AND RQST_DTL_SNO = #{rqstDtlSno}

DELETE FROM WAQ_DDL_IDX_COL
 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_IDX_COL
 WHERE RQST_NO      = #{rqstNo,jdbcType=VARCHAR}
   AND RQST_SNO     = #{rqstSno,jdbcType=DECIMAL}
   AND RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_DDL_IDX_COL 
     SET DDL_IDX_COL_ID = #{ddlIdxColId,jdbcType=VARCHAR}
	 WHERE RQST_NO          = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO         = #{rqstSno,jdbcType=DECIMAL}
     AND RQST_DTL_SNO     = #{rqstDtlSno,jdbcType=DECIMAL}	
     AND RQST_DTL_DTL_SNO = #{rqstDtlDtlSno,jdbcType=DECIMAL}
