mapper.select:
select 

,

from WAQ_DDL_TBL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_TBL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
	--DDL 스크립트 업데이트...
	UPDATE WAQ_DDL_TBL
	   SET SCRT_INFO = #{scrtInfo,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

	UPDATE WAQ_DDL_TBL X
     SET TBL_CHG_TYP_CD = NULL      
   WHERE RQST_NO = #{rqstNo}

   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_DDL_TBL SET DDL_TBL_ID = #{ddlTblId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_DDL_TBL (DDL_TBL_ID
) SELECT DDL_TBL_ID
FROM WAQ_DDL_TBL A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')
