mapper.select:
select 

from WAQ_DDL_TSF_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_TSF_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_TSF_COL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
--DATA TYPE 업데이드 여부 UPDATE WAQ_DDL_TSF_COL A SET DATA_TYPE_UPD_YN = 'Y' WHERE RQST_NO = #{rqstNo} AND REG_TYP_CD = 'U' AND EXISTS ( SELECT 1 FROM WAQ_DDL_TSF_TBL B JOIN WAM_DDL_TSF_TBL C ON C.DDL_TBL_PNM = B.DDL_TBL_PNM
AND B.RQST_NO = #{rqstNo} JOIN WAM_DDL_TSF_COL D ON C.DDL_TBL_ID = D.DDL_TBL_ID AND D.REG_TYP_CD in ('C','U') WHERE B.RQST_NO = A.RQST_NO AND B.RQST_SNO = A.RQST_SNO AND D.DDL_COL_PNM = A.DDL_COL_PNM AND D.DDL_COL_LNM = A.DDL_COL_LNM AND NOT( COALESCE(A.DATA_TYPE, '_') = COALESCE(D.DATA_TYPE, '_') AND COALESCE(A.DATA_LEN, 0) = COALESCE(D.DATA_LEN, 0) AND COALESCE(A.DATA_SCAL, 0) = COALESCE(D.DATA_SCAL, 0) )
)

--NOT NULL 업데이트 여부 UPDATE WAQ_DDL_TSF_COL A SET NONUL_UPD_YN = 'Y' WHERE RQST_NO = #{rqstNo} AND REG_TYP_CD = 'U' AND EXISTS ( SELECT 1 FROM WAQ_DDL_TSF_TBL B JOIN WAM_DDL_TSF_TBL C ON C.DDL_TBL_PNM = B.DDL_TBL_PNM
AND B.RQST_NO = #{rqstNo} JOIN WAM_DDL_TSF_COL D ON C.DDL_TBL_ID = D.DDL_TBL_ID AND D.REG_TYP_CD IN ('C','U') WHERE B.RQST_NO = A.RQST_NO AND B.RQST_SNO = A.RQST_SNO AND D.DDL_COL_PNM = A.DDL_COL_PNM AND D.DDL_COL_LNM = A.DDL_COL_LNM AND NOT( COALESCE(A.NONUL_YN, '_') = COALESCE(D.NONUL_YN, '_') )
)

--Default value 업데이트 여부 UPDATE WAQ_DDL_TSF_COL A SET DEFLT_VAL_UPD_YN = 'Y' WHERE RQST_NO = #{rqstNo} AND REG_TYP_CD = 'U' AND EXISTS ( SELECT 1 FROM WAQ_DDL_TSF_TBL B JOIN WAM_DDL_TSF_TBL C ON C.DDL_TBL_PNM = B.DDL_TBL_PNM
AND B.RQST_NO = #{rqstNo} JOIN WAM_DDL_TSF_COL D ON C.DDL_TBL_ID = D.DDL_TBL_ID AND D.REG_TYP_CD in ('C','U') WHERE B.RQST_NO = A.RQST_NO AND B.RQST_SNO = A.RQST_SNO AND D.DDL_COL_PNM = A.DDL_COL_PNM AND D.DDL_COL_LNM = A.DDL_COL_LNM AND NOT( COALESCE(A.DEFLT_VAL, '_') = COALESCE(D.DEFLT_VAL, '_') )
)
