mapper.select:
select 

from WAQ_DDL_TSF_IDX_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_DDL_TSF_IDX_COL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

DELETE FROM WAQ_DDL_TSF_IDX_COL
 WHERE RQST_NO  = #{rqstNo}
   AND RQST_SNO = #{rqstSno}

mapper.select:
SELECT
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' END AS FontColor , U.USER_NM AS RQST_USER_NM FROM WAQ_DDL_TSF_IDX_COL A LEFT OUTER JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
ORDER BY A.RQST_SNO, A.DDL_IDX_COL_ORD

SELECT A.* , B.DDL_IDX_PNM , B.DDL_IDX_LNM , C.DDL_TBL_PNM , C.DDL_TBL_LNM , U.USER_NM AS FRS_RQST_USER_NM , U1.USER_NM AS RQST_USER_NM , U2.USER_NM AS APRV_USER_NM , CASE WHEN A.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND A.REG_TYP_CD IN ('C', 'U') THEN '#0000FF' ELSE NULL END AS FontColor FROM WAH_DDL_TSF_IDX_COL A LEFT JOIN WAH_DDL_TSF_IDX B ON A.DDL_IDX_ID = B.DDL_IDX_ID AND B.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT JOIN WAM_DDL_TBL C ON B.DDL_TBL_ID = C.DDL_TBL_ID AND C.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAA_USER U ON A.FRS_RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U1 ON A.RQST_USER_ID = U1.USER_ID AND U1.REG_TYP_CD IN ('C', 'U') AND U1.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') LEFT OUTER JOIN WAA_USER U2 ON A.APRV_USER_ID = U2.USER_ID AND U2.REG_TYP_CD IN ('C', 'U') AND U2.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD')
AND B.DDL_IDX_ID = #{ddlIdxId}
AND C.DDL_TBL_ID = #{ddlTblId}
ORDER BY A.EXP_DTM DESC, A.STR_DTM DESC
