mapper.select:
select 

from WAQ_COD_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_COD_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
-- 코드매핑정의서 요청 상세정보 조회
select 

from WAQ_COD_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

--코드매핑정의서 타겟코드도메인 리스트 조회 SELECT A.DMN_ID AS TGT_DMN_ID, G.DMNG_LNM AS TGT_DMNG_LNM, A.DMN_LNM AS TGT_DMN_LNM, A.DMN_PNM AS TGT_DMN_PNM, D.CD_VAL AS TGT_CD_VAL, D.CD_VAL_NM AS TGT_CD_VAL_NM, B.DATA_TYPE AS TGT_DATA_TYPE, B.DATA_LEN AS TGT_DATA_LEN, A.CD_VAL_TYP_CD AS TGT_CD_VAL_TYP_CD, A.CD_VAL_IVW_CD AS TGT_CD_VAL_IVW_CD FROM WAM_CD_VAL D INNER JOIN WAM_DMN A ON A.DMN_ID = D.DMN_ID INNER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID INNER JOIN WAA_INFO_TYPE B ON A.INFOTP_ID = B.INFOTP_ID AND B.EXP_DTM = TO_DATE('99991231','YYYYMMDD')
AND G.FULL_PATH LIKE '코드%'
AND (A.DMN_LNM LIKE '%' || #{tgtDmnLnm,jdbcType=VARCHAR} || '%' OR A.DMN_PNM LIKE '%' || #{tgtDmnLnm,jdbcType=VARCHAR} || '%')
AND D.CD_VAL = #{tgtCdVal,jdbcType=VARCHAR}
AND A.CD_VAL_TYP_CD = #{tgtCdValTypCd,jdbcType=VARCHAR}
AND A.CD_VAL_IVW_CD = #{tgtCdValIvwCd,jdbcType=VARCHAR}
ORDER BY A.DMN_LNM, D.CD_VAL

mapper.delete:
DELETE FROM WAQ_COD_MAP
 WHERE RQST_NO  = #{rqstNo}
   AND RQST_SNO = #{rqstSno}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_COD_MAP SET CD_MAP_ID = #{cdMapId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
--코드매핑정의서 테이블 추가대상 조회... SELECT ((SELECT COALESCE(MAX(RQST_SNO), 0) FROM WAQ_COD_MAP WHERE RQST_NO = #{reqmst.rqstNo}) + (ROW_NUMBER() OVER())) AS RQST_SNO , 'I' AS IBS_STATUS , CD_MAP_ID , #{reqmst.rqstDcd} AS RQST_DCD , OBJ_VERS, OBJ_DESCN, REG_TYP_CD, FRS_RQST_DTM, FRS_RQST_USER_ID, RQST_DTM, RQST_USER_ID, APRV_DTM, APRV_USER_ID, MAP_DF_TYPE, CD_CNVS_TYPE, CD_MAP_TYPE, TGT_DMN_ID, TGT_DMN_PNM, TGT_DMN_LNM, TGT_CD_VAL, TGT_CD_VAL_NM, SRC_DB_ID, SRC_DB_PNM, SRC_TBL_ID, SRC_TBL_LNM, SRC_TBL_PNM, SRC_COL_LNM, SRC_COL_PNM, SRC_CD_VAL, SRC_CD_VAL_NM, A.SRC_BIZ_ID, A.SRC_BIZ_NM, A.SRC_SYS_ID, A.SRC_SYS_NM, A.SRC_UPP_CD_VAL, A.SRC_UPP_CD_VAL_NM, A.APP_CRGP_ID, A.APP_CRGP_NM, A.CNVS_CRGP_ID, A.CNVS_CRGP_NM, A.SRC_CD_DMN_CD FROM WAM_COD_MAP A WHERE A.CD_MAP_ID IN
#{item.cdMapId}
AND A.REG_TYP_CD IN ('C', 'U')
