mapper.select:
select 

from WAQ_ASIS_DMN_CD_VAL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_ASIS_DMN_CD_VAL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
-- ASIS 유효값 요청 상세정보 조회
select 

from WAQ_ASIS_DMN_CD_VAL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

--물리모델 테이블 추가대상 조회... SELECT ((SELECT COALESCE(MAX(RQST_SNO), 0) FROM WAQ_ASIS_DMN_CD_VAL WHERE RQST_NO = #{reqmst.rqstNo}) + (ROW_NUMBER() OVER())) AS RQST_SNO , 'I' AS IBS_STATUS , A.DMN_ID, A.DMN_LNM, A.DMN_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.ASIS_DIT, A.ASIS_SYSTEM_NM, A.ASIS_GROUP_NM, A.ASIS_PART_NM , A.ASIS_BIZ_NM, A.ASIS_BIZ_CD, A.ASIS_DB_NM, A.ASIS_TBL_NM, A.ASIS_COL_ID, A.ASIS_COL_NM, A.CODE_NM , A.DFD_COL_NM, A.DFD_COL_VALUE, A.CODE_VALUE, A.CODE_DISP_NUM, A.APP_TEAM_NM, A.APP_CHRG_NM, A.TSF_TEAM_NM , A.TSF_CHRG_NM, A.LAST_CHG_DT, A.ADD_NOTE1, A.ADD_NOTE2, A.ADD_NOTE3, A.ADD_NOTE4, A.APP_SI_NM FROM WAM_ASIS_DMN_CD_VAL A WHERE A.DMN_ID IN
#{item.dmnId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_ASIS_DMN_CD_VAL 
      SET DMN_ID = #{dmnId,jdbcType=VARCHAR}
	  WHERE RQST_NO    = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO   = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_ASIS_DMN_CD_VAL ( DMN_ID
) SELECT DMN_ID
FROM WAQ_ASIS_DMN_CD_VAL A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_ASIS_DMN_CD_VAL ( DMN_ID , EXP_DTM , STR_DTM
) SELECT A.DMN_ID AS DMN_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_ASIS_DMN_CD_VAL A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'
