mapper.select:
select 

from WAQ_PDM_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and PDM_REL_PNM = #{pdmRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

mapper.delete:
delete from WAQ_PDM_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}
  and PDM_REL_PNM = #{pdmRelPnm,jdbcType=VARCHAR}
  and PA_ENTY_PNM = #{paEntyPnm,jdbcType=VARCHAR}
  and PA_ATTR_PNM = #{paAttrPnm,jdbcType=VARCHAR}
  and CH_ENTY_PNM = #{chEntyPnm,jdbcType=VARCHAR}
  and CH_ATTR_PNM = #{chAttrPnm,jdbcType=VARCHAR}

mapper.select:
SELECT
, CASE WHEN A.VRF_CD = '2' THEN '#FF0000' END AS FontColor , U.USER_NM AS RQST_USER_NM , A.PDM_REL_PNM AS BF_PDM_REL_PNM , A.PA_ENTY_PNM AS BF_PA_ENTY_PNM , A.PA_ATTR_PNM AS BF_PA_ATTR_PNM , A.CH_ENTY_PNM AS BF_CH_ENTY_PNM , A.CH_ATTR_PNM As BF_CH_ATTR_PNM FROM WAQ_PDM_REL A INNER JOIN WAQ_PDM_TBL B ON A.RQST_NO = B.RQST_NO AND A.RQST_SNO = B.RQST_SNO LEFT OUTER JOIN WAA_USER U ON A.RQST_USER_ID = U.USER_ID AND U.REG_TYP_CD IN ('C', 'U') AND U.EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') WHERE A.RQST_NO = #{rqstNo}
AND A.RQST_SNO = #{rqstSno}
ORDER BY A.RQST_SNO, A.RQST_DTL_SNO, A.PA_SUBJ_PNM, A.PA_ENTY_PNM, A.PA_ATTR_PNM

select 

from WAQ_PDM_REL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
  and RQST_DTL_SNO = #{rqstDtlSno,jdbcType=DECIMAL}

mapper.delete:
DELETE FROM WAQ_PDM_REL
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
   --신규 적재 대상 ID 업데이트
   UPDATE WAQ_PDM_REL SET PDM_REL_ID = #{pdmRelId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
     AND RQST_DTL_SNO = #{rqstDtlSno}

mapper.select:
SELECT A.*
  FROM WAQ_PDM_REL A
 WHERE A.PDM_REL_PNM = #{pdmRelPnm}
   AND A.PA_SUBJ_PNM = #{paSubjPnm}
   AND A.PA_ENTY_PNM = #{paEntyPnm}
   AND A.PA_ATTR_PNM = #{paAttrPnm}
   AND A.CH_SUBJ_PNM = #{chSubjPnm}
   AND A.CH_ENTY_PNM = #{chEntyPnm}
   AND A.CH_ATTR_PNM = #{chAttrPnm}
   AND A.RQST_NO = #{rqstNo}
   AND A.RQST_SNO = #{rqstSno}
