mapper.update:
	UPDATE WAQ_DMN SET DMN_ID = #{dmnId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

UPDATE WAQ_DMN X SET (DMNG_ID, INFOTP_ID, SUBJ_ID, LST_ENTY_ID, LST_ATTR_ID) = (
SELECT MAX(B.DMNG_ID), MAX(C.INFOTP_ID), MAX(D.SUBJ_ID), MAX(E.PDM_TBL_ID), MAX(F.PDM_COL_ID) FROM WAQ_DMN A LEFT OUTER JOIN WAA_DMNG B ON A.DMNG_LNM = B.DMNG_LNM AND B.EXP_DTM = TO_DATE( '9999-12-31', 'YYYY-MM-DD') AND A.STND_ASRT = B.STND_ASRT JOIN ( SELECT CASE WHEN BSC_LVL IS NULL THEN 1 ELSE BSC_LVL END AS BSC_LVL FROM WAA_BSC_LVL WHERE EXP_DTM = TO_DATE('9999-12-31', 'YYYY-MM-DD') AND REG_TYP_CD IN ('C', 'U') AND BSC_ID = 'DMNG' ) L ON B.DMNG_LVL = L.BSC_LVL LEFT OUTER JOIN WAA_INFO_TYPE C ON A.INFOTP_LNM = C.INFOTP_LNM AND C.EXP_DTM = TO_DATE( '9999-12-31', 'YYYY-MM-DD') AND A.STND_ASRT = C.STND_ASRT LEFT OUTER JOIN WAA_SUBJ D ON A.FULL_PATH = D.FULL_PATH AND D.EXP_DTM = TO_DATE( '9999-12-31', 'YYYY-MM-DD') AND D.REG_TYP_CD IN ('C', 'U') LEFT OUTER JOIN WAM_PDM_TBL E ON A.LST_ENTY_PNM = E.PDM_TBL_PNM AND A.LST_ENTY_LNM = E.PDM_TBL_LNM LEFT OUTER JOIN WAM_PDM_COL F ON A.LST_ATTR_PNM = F.PDM_COL_PNM AND A.LST_ATTR_LNM = F.PDM_COL_LNM WHERE A.RQST_NO = X.RQST_NO AND A.RQST_SNO = X.RQST_SNO
) WHERE RQST_NO = #{rqstNo} AND RVW_STS_CD = '1'

mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_DMN ( DMN_ID
) SELECT DMN_ID
FROM WAQ_DMN A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_DMN ( DMN_ID , EXP_DTM , STR_DTM
) SELECT A.DMN_ID AS DMN_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_DMN A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'


  	  --검증쿼리
      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND NULLIF(D.DMN_PNM, '') IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )


  	  --검증쿼리
      AND A.RQST_DCD = 'CU'
       AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND NULLIF(D.LNM_CRI_DS, '') IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )


  	  --검증쿼리
      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1
			            FROM WAQ_DMN D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.DMN_LNM = D.DMN_LNM
			              AND A.STND_ASRT = D.STND_ASRT
			              AND NULLIF(D.PNM_CRI_DS, '') IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )

--검증쿼리 AND A.RQST_DCD = 'CU' AND EXISTS (SELECT 1 FROM WAQ_DMN D INNER JOIN WAA_DMNG G ON D.RQST_NO = #{rqstNo}
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND D.DMNG_ID = G.DMNG_ID AND D.CD_VAL_TYP_CD IN ('O', 'C') AND G.EXP_DTM = TO_DATE('99991231', 'YYYYMMDD') AND G.CD_DMN_YN ='Y'
LEFT OUTER JOIN WAQ_CD_VAL V ON D.RQST_NO = V.RQST_NO AND D.RQST_SNO = V.RQST_SNO AND V.DMN_LNM = D.DMN_LNM AND V.STND_ASRT = D.STND_ASRT WHERE NULLIF(V.DMN_LNM, '') IS NULL )


  	  --검증쿼리
      AND A.RQST_DCD = 'CU'
      AND EXISTS (SELECT 1 FROM WAQ_DMN D
                  WHERE D.RQST_NO = #{rqstNo}

				     AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

                  AND DATA_LEN IN  (64,128,256)
                  AND ENC_YN = 'N'
			    )

--검증쿼리 AND A.REG_TYP_CD = 'C' AND EXISTS (SELECT 1 FROM WAQ_DMN D INNER JOIN WAA_GOV_DMN E ON E.DMN_LNM = D.DMN_LNM AND E.REG_TYP_CD IN ('C', 'U') WHERE D.RQST_NO = #{rqstNo} AND A.DMN_LNM = D.DMN_LNM AND A.STND_ASRT = D.STND_ASRT --표준구분추가 이베이코리아 190111
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
)

mapper.select:
SELECT * FROM( SELECT A.DMN_LNM , A.DMN_PNM , A.LNM_CRI_DS , A.DMNG_ID , A.INFOTP_ID , A.UPP_DMN_ID , A.SUBJ_ID , A.LST_ENTY_ID , A.LST_ENTY_PNM , A.LST_ENTY_LNM , A.LST_ATTR_ID , A.LST_ATTR_PNM , A.LST_ATTR_LNM , A.CD_VAL_TYP_CD , A.CD_VAL_IVW_CD , A.SDITM_AUTO_CRT_YN , A.DATA_FRM , A.CRG_USER_ID , A.DMN_ORG_DS , A.OBJ_DESCN , A.FULL_PATH , A.PNM_CRI_DS , A.ENC_YN , A.DMN_DSCD , A.TRANS_YN , B.DATA_TYPE , B.DATA_LEN , B.DATA_SCAL ,C.CD_VAL_ID, C.CD_VAL , C.CD_VAL_NM, A.DMN_ORG_TXT,A.SUB_CD_YN, A.STND_ASRT FROM WAM_DMN A INNER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID AND A.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_INFO_TYPE B ON A.INFOTP_ID = B.INFOTP_ID AND B.EXP_DTM = TO_DATE('99991231','YYYYMMDD') INNER JOIN WAM_CD_VAL C ON A.DMN_ID = C.DMN_ID AND C.REG_TYP_CD IN ('C', 'U') UNION SELECT A.DMN_LNM , A.DMN_PNM , A.LNM_CRI_DS , A.DMNG_ID , A.INFOTP_ID , A.UPP_DMN_ID , A.SUBJ_ID , A.LST_ENTY_ID , A.LST_ENTY_PNM , A.LST_ENTY_LNM , A.LST_ATTR_ID , A.LST_ATTR_PNM , A.LST_ATTR_LNM , A.CD_VAL_TYP_CD , A.CD_VAL_IVW_CD , A.SDITM_AUTO_CRT_YN , A.DATA_FRM , A.CRG_USER_ID , A.DMN_ORG_DS , A.OBJ_DESCN , A.FULL_PATH , A.PNM_CRI_DS , A.ENC_YN , A.DMN_DSCD , A.TRANS_YN , B.DATA_TYPE , B.DATA_LEN , B.DATA_SCAL ,C.CD_VAL_ID, C.CD_VAL , C.CD_VAL_NM, A.DMN_ORG_TXT,A.SUB_CD_YN, A.STND_ASRT FROM WAQ_DMN A INNER JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID AND A.REG_TYP_CD IN ('C', 'U') INNER JOIN WAA_INFO_TYPE B ON A.INFOTP_ID = B.INFOTP_ID AND B.EXP_DTM = TO_DATE('99991231','YYYYMMDD') INNER JOIN WAQ_CD_VAL C ON A.DMN_LNM = C.DMN_LNM AND A.STND_ASRT = C.STND_ASRT AND C.REG_TYP_CD IN ('C', 'U') AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR} ) DMN
AND (DMN_LNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_PNM LIKE '%' || #{dmnLnm,jdbcType=VARCHAR} || '%' OR DMN_LNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%' OR DMN_PNM LIKE '%' || UPPER(#{dmnLnm,jdbcType=VARCHAR}) || '%')
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND OBJ_DESCN LIKE '%' || #{objDescn,jdbcType=VARCHAR} || '%'
AND STND_ASRT = #{stndAsrt,jdbcType=VARCHAR}
ORDER BY DMN_LNM, CD_VAL
