mapper.select:
select 

from WAQ_ERR_MSG_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAQ_ERR_MSG_MAP
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.select:
    SELECT A.*
	  FROM WAQ_ERR_MSG_MAP A
	 WHERE A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
	   AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT ((SELECT COALESCE(MAX(RQST_SNO), 0) FROM WAQ_ERR_MSG_MAP WHERE RQST_NO = #{reqmst.rqstNo}) + (ROW_NUMBER() OVER())) AS RQST_SNO , 'I' AS IBS_STATUS , A.MSG_MAP_ID, A.MSG_MAP_LNM, A.MSG_MAP_PNM , #{reqmst.rqstDcd} AS RQST_DCD , A.OBJ_DESCN, A.OBJ_VERS , A.STD_ERR_MSG_ID, A.ERR_CD, A.ISD_ERR_CAS_CTS, A.OSD_ERR_CAS_CTS, A.DMN_ID, A.DMN_LNM, A.DMN_PNM, A.CD_VAL_ID, A.CD_VAL, A.CD_VAL_NM, A.ISTT_US_IMPO_YN, A.RPST_ERCD_YN FROM WAM_ERR_MSG_MAP A WHERE A.MSG_MAP_ID IN
#{item.msgMapId}
AND A.REG_TYP_CD IN ('C', 'U')

mapper.update:
 	UPDATE WAQ_ERR_MSG_MAP SET MSG_MAP_ID = #{msgMapId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
