mapper.select:
SELECT 

FROM WAQ_STWD
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

SELECT A.STWD_LNM, A.STWD_PNM
FROM WAM_STWD A
WHERE A.STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}
  AND A.REG_TYP_CD in ('C','U')

  AND A.STND_ASRT = #{stndAsrt, jdbcType=VARCHAR} 

  AND NOT EXISTS (SELECT 1 FROM WAQ_STWD B
                   WHERE B.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
                     AND A.STWD_LNM = B.STWD_LNM
                     AND A.STWD_PNM = B.STWD_LNM
                     AND A.STND_ASRT = B.STND_ASRT
                     )
UNION ALL
SELECT STWD_LNM, STWD_PNM
  FROM WAQ_STWD
 WHERE RQST_NO  = #{rqstNo,jdbcType=VARCHAR}
   AND STWD_LNM = #{stwdLnm,jdbcType=VARCHAR}

   AND STND_ASRT = #{stndAsrt, jdbcType=VARCHAR} 

   AND RQST_DCD = 'CU'

mapper.delete:
DELETE FROM WAQ_STWD
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

DELETE FROM WAQ_STWD
WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.insert:
INSERT INTO WAQ_STWD (RQST_NO, RQST_SNO, STWD_ID, STWD_LNM, STWD_PNM, RQST_DCD,ENG_MEAN, CCH_NM, ORG_DS, OBJ_DESCN, OBJ_VERS, WD_DCD, RQST_DTM , RQST_USER_ID, DMN_YN, STND_ASRT) SELECT #{reqmst.rqstNo} , (SELECT COALESCE(MAX(RQST_SNO), 0) FROM WAQ_STWD WHERE RQST_NO = #{reqmst.rqstNo}) + (ROW_NUMBER() OVER()) , STWD_ID, STWD_LNM, STWD_PNM , #{reqmst.rqstDcd} AS RQST_DCD ,ENG_MEAN, CCH_NM, ORG_DS, OBJ_DESCN, OBJ_VERS, WD_DCD , NOW() , #{reqmst.rqstUserId,jdbcType=VARCHAR} , DMN_YN , STND_ASRT FROM WAM_STWD WHERE STWD_ID IN
#{item.stwdId}
AND REG_TYP_CD IN ('C' , 'U')

mapper.update:
UPDATE WAQ_STWD
  SET STWD_LNM     = REPLACE(#{stwdLnm,jdbcType=VARCHAR},' ','')
    , STWD_PNM     = UPPER(REPLACE(#{stwdPnm,jdbcType=VARCHAR},' ',''))
    , RQST_DCD     = #{rqstDcd,jdbcType=VARCHAR}
    , ENG_MEAN     = #{engMean,jdbcType=VARCHAR}
    , CCH_NM       = #{cchNm,jdbcType=VARCHAR}
    , ORG_DS       = #{orgDs,jdbcType=VARCHAR}
    , OBJ_DESCN    = #{objDescn,jdbcType=VARCHAR}
    , WD_DCD       = #{wdDcd,jdbcType=VARCHAR}
    , RQST_DTM     = NOW()
    , RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
    , DMN_YN       =  #{dmnYn,jdbcType=VARCHAR}
    , STND_ASRT    = #{stndAsrt,jdbcType=VARCHAR}

WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

	UPDATE WAQ_STWD 
	   SET STWD_ID = #{stwdId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
     AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

