mapper.insert:
--WAM 추가 (WAQ 내용을 추가, REG_TYP_CD 가 신규,변경일 경우) INSERT INTO WAM_STWD ( STWD_ID
) SELECT STWD_ID
FROM WAQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1' AND A.REG_TYP_CD IN ('C', 'U')

--WAH이력 적재... INSERT INTO WAH_STWD ( STWD_ID , EXP_DTM , STR_DTM
) SELECT A.STWD_ID AS STWD_ID , TO_DATE( '9999-12-31', 'YYYY-MM-DD') AS EXP_DTM , NOW() AS STR_DTM
FROM WAQ_STWD A WHERE A.RQST_NO = #{rqstNo} AND A.RVW_STS_CD = '1'


	--검증쿼리
	  AND A.REG_TYP_CD = 'C'   
      AND EXISTS (SELECT 1
			            FROM WAQ_STWD D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.STWD_LNM = D.STWD_LNM
			              AND A.STND_ASRT = D.STND_ASRT  --표준구분추가 이베이코리아 190111
			              AND NULLIF(D.STWD_PNM, '') IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )


	--검증쿼리
	  AND A.REG_TYP_CD = 'C'   
      AND EXISTS (SELECT 1
			            FROM WAQ_STWD D
			            WHERE D.RQST_NO = #{rqstNo}
			              AND A.STWD_LNM = D.STWD_LNM
			              AND A.STND_ASRT = D.STND_ASRT  --표준구분추가 이베이코리아 190111
			              AND NULLIF(D.OBJ_DESCN, '') IS NULL

						  AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

			           )

--검증쿼리 AND A.REG_TYP_CD = 'C' AND EXISTS (SELECT 1 FROM WAQ_STWD D INNER JOIN WAA_GOV_STWD E ON E.STWD_LNM = D.STWD_LNM AND E.REG_TYP_CD IN ('C', 'U') WHERE D.RQST_NO = #{rqstNo} AND A.STWD_LNM = D.STWD_LNM AND A.STND_ASRT = D.STND_ASRT --표준구분추가 이베이코리아 190111
AND D.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
)
